<?phpif (!defined('ABSPATH'))    exit; // Exit if accessed directlyadd_action("wp_ajax_sliding-captcha", "sliding_captcha_fn");add_action("wp_ajax_nopriv_sliding-captcha", "sliding_captcha_fn");function sliding_captcha_fn() {    $Response['error'] = false;    $action = sanitize_text_field($_POST["action"]);    $qaptcha_key = sanitize_text_field($_POST["qaptcha_key"]);    if (isset($_POST['action']) && isset($_POST['qaptcha_key'])) {        if (htmlentities($action, ENT_QUOTES, 'UTF-8') == 'sliding-captcha') {            echo json_encode($Response);        } else {            $Response['error'] = true;            echo json_encode($Response);            exit;        }    } else {        $Response['error'] = true;        echo json_encode($Response);        exit;    }    die;}